import { useState, useEffect } from "react"

function Dice({players, updatePlayerPosition, winner, index, setIndex, socket}){
    const [rolledNum, setRolledNum] = useState(0);
    const size = players.length;

    useEffect(() => {
        if (players.length > 0) {
          const playerIndex = players.findIndex(player => player.socketId === socket.id);
          setIndex(playerIndex);
          setYourTurn(turnIndex === playerIndex);
        }
      }, [players, turnIndex]);


    function roll() {
        if (yourTurn) {
            const rolled = Math.floor(Math.random() * 6) + 1;
            setRolledNum(rolled);
            socket.emit('roll_dice', { room: 'room_id', rolledNum: rolled }); // Replace 'room_id' with the actual room ID
        }
    }

    return (
        <div className="diceBody">
            {winner === '' ? (
                size > 1 ? ( 
                    <>
                        <div className="numberRolled">Number: {rolledNum}</div>
                        <div className="playerCall">Player {index + 1}'s turn!</div>
                        <button onClick={roll} className="roll">ROLL</button>
                    </>
                ) : (
                    <div>Add players to start</div>
                )
            ) : (
                <div>{winner} is the winner!</div>
            )}
        </div>
    );
}

export default Dice;
