import React, { useState, useEffect } from 'react';
import './dice.css';
import { socket } from '../App';  // Assuming you have socket in the main App

function Dice({ players, updatePlayerPosition, currentTurn }) {
  const [rolledNum, setRolledNum] = useState(0);

  // Emit the dice roll to other players
  const rollDice = () => {
    const rolled = Math.floor(Math.random() * 6) + 1;
    setRolledNum(rolled);
    socket.emit('dice_roll', { rolled, currentTurn });
  };

  // Listen for dice rolls from the server
  useEffect(() => {
    socket.on('dice_roll', (data) => {
      setRolledNum(data.rolled);
      updatePlayerPosition(data.currentTurn, data.rolled);
    });
  }, [updatePlayerPosition]);

  return (
    <div className="diceBody">
      {players.length > 1 ? (
        <>
          <div className="numberRolled">Number: {rolledNum}</div>
          <div className="playerCall">Player {currentTurn + 1}'s turn!</div>
          <button onClick={rollDice} className="roll">ROLL</button>
        </>
      ) : (
        <div>Add players to start</div>
      )}
    </div>
  );
}

export default Dice;
