import React from 'react';
import './grid.css';

function Grid({ players }) {
    const grid = generateGrid();

    return (
        <div className='grid'>
            {grid.map((number, index) => (
                <Square key={index} number={number} players={players} />
            ))}
        </div>
    );
}

function generateGrid() {
    let size = 10;
    let squares = [];
    let counter = size * size;

    for (let row = 0; row < size; row++) {
        let rowSquares = [];
        for (let col = 0; col < size; col++) {
            rowSquares.push(counter);
            counter--;
        }
        if (row % 2 === 1) {
            rowSquares.reverse();
        }
        squares = squares.concat(rowSquares);
    }
    return squares;
}

function Square({ number, players }) {
    const playerOnSquare = players.find(player => player.position === number);
    const backgroundColor = playerOnSquare ? playerOnSquare.color : 'transparent';

    return (
        <button className='square' style={{ backgroundColor }}></button>
    );
}

export default Grid;
