import React, { useState, useEffect } from 'react';

function Dice({ socket, room, players, currentPlayerIndex, isMyTurn, updatePlayerPosition }) {
    const [rolledNum, setRolledNum] = useState(0);

    const roll = () => {
        if (isMyTurn) {
            const rolled = Math.floor(Math.random() * 6) + 1;
            setRolledNum(rolled);
            socket.emit('dice_roll', { room, rolled });
        }
    };

    useEffect(() => {
        socket.on('dice_rolled', ({ rolled }) => {
            setRolledNum(rolled);
            updatePlayerPosition(currentPlayerIndex, rolled);
        });

        return () => {
            socket.off('dice_rolled');
        };
    }, [socket, currentPlayerIndex, updatePlayerPosition]);

    return (
        <div className="diceBody">
            <div className="numberRolled">Number: {rolledNum}</div>
            <div className="playerCall">Player {players[currentPlayerIndex]?.name}'s turn!</div>
            <button onClick={roll} className="roll" disabled={!isMyTurn}>ROLL</button>
        </div>
    );
}

export default Dice;
