import { useState, useEffect } from 'react';
import './App.css';
import io from 'socket.io-client';
import Grid from './components/grid';
import Dice from './components/dice';

const socket = io.connect('http://localhost:3001');

function App() {
  const [username, setUsername] = useState('');
  const [room, setRoom] = useState('');
  const [showGame, setShowGame] = useState(false);
  const [players, setPlayers] = useState([]);
  const [color, setColor] = useState('#000000');
  const [myIndex, setMyIndex] = useState(0);
  const [turnIndex, setTurnIndex] = useState(null);
  const [winner, setWinner] = useState('');

  useEffect(() => {
    socket.on('players_update', (playersData) => {
      setPlayers(playersData);
      const myPlayerIndex = playersData.findIndex(player => player.socketId === socket.id);
      setMyIndex(myPlayerIndex);
    });

    socket.on('turn_update', (newTurnIndex) => {
      setTurnIndex(newTurnIndex);
    });

    socket.on('game_winner', (winner) => {
      setWinner(winner);
    });

    return () => {
      socket.off('players_update');
      socket.off('turn_update');
      socket.off('game_winner');
    };
  }, []);

  const joinRoom = () => {
    if (username !== '' && room !== '') {
      const player = {
        name: username,
        color: color,
        position: 0,
      };
      socket.emit('join_room', { room, player });
      setShowGame(true);
    }
  };

  return (
    <div className="App">
      {!showGame ? (
        <div>
          <h3>Join game</h3>
          <input
            type="text"
            placeholder="Name"
            onChange={(event) => { setUsername(event.target.value); }}
          />
          <input
            type="text"
            placeholder="Room"
            onChange={(event) => { setRoom(event.target.value); }}
          />
          <input
            type="color"
            placeholder="Choose color"
            onChange={(event) => { setColor(event.target.value); }}
          />
          <button onClick={joinRoom}>Join</button>
        </div>
      ) : (
        <div>
          <Grid players={players} />
          <Dice players={players} index={myIndex} setIndex={setMyIndex} turnIndex={turnIndex} socket={socket} winner={winner} room={room} />
        </div>
      )}
    </div>
  );
}

export default App;
